var createNewRecords = Class.create();
createNewRecords.prototype = {
    initialize: function() {
        this.num_of_Records = gs.getProperty('x_569831_iss_track.Number of Records', 10);
        gd = new GlideDate();
        this.date = gd.getValue();
    },

    create: function() {



        grCreate = new GlideRecord('x_569831_iss_track_issues');

        for (var i = 0; i < this.num_of_Records; i++) {
            grCreate.initialize();
            grCreate.u_issue_property = 1;
            grCreate.u_issue_status = 2;
            grCreate.u_short_description = 'Created by Sceduled Job on: ';
            grCreate.insert();

        }

    },

    type: 'createNewRecords'
};
